        /// <summary>
        /// Normalize the pasted base64:
        /// - Remove anything not in the base64 alphabet (A–Z a–z 0–9 + / =).
        ///   This tolerates line breaks, spaces, tabs, comments accidentally pasted, etc.
        /// </summary>
        private static string CleanBase64(string raw)
        {
            if (string.IsNullOrEmpty(raw))
                return string.Empty;
            // Remove all non-base64 chars (safer than whitespace-only trimming)
            // Regex explanation: keep [A-Za-z0-9+/=], drop everything else.
            return Regex.Replace(raw, @"[^A-Za-z0-9\+/=]", "");
        }
